import React from "react";
import "./Testimonial.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Pagination } from "swiper";
import "swiper/css/pagination";
import { Autoplay } from "swiper";
import "swiper/css/autoplay";
// import shehzad from "../../img/tshahzad.jpg";
// import adnan from "../../img/testadnan.jpg";
// import saftain from "../../img/saftaincpy.jpg";
// import yasir from "../../img/yasircpy.jpg";

const Testimonial = () => {
  const clients = [
    {
      // img: shehzad,
      cname: "Muhammad Shehzad",
      dsg: "MIT Director, Mentor",
      desc: "I appreciate that my dedicated and skilled student is committed to doing their best, who excels in their work. A pleasure to teach.",
    },
    {
      // img: adnan,
      cname: "Muhammad Adnan",
      dsg: "Web Developer",
      desc: "I just wanted to share a quick note and let you know that this guy did an excellent job. I’m glad I decided to work with you.",
    },
    {
      // img: saftain,
      cname: "Saftain Noor",
      dsg: "Client",
      desc:"I recommend Junaid for delivering a top-notch, SEO-friendly website and exceptional collaboration.",
    },
    {
      // img: yasir,
      cname: "Yasir M.",
      dsg: "Cyber Security Expert",
      desc: "He made sure that all the changes we wanted for our website were implemented flawlessly and the site was delivered on time.",
    },
  ];

  return (
    <div className="t-wrapper" id="testimonial">
      <div className="t-heading">
        <span>Clients </span>
        <span>Love </span>
        <span>My Work</span>
      </div>
      <Swiper
        modules={[Pagination, Autoplay]}
        slidesPerView={1}
        pagination={{ clickable: true }}
        autoplay={true}
      >
        {clients.map((client, index) => {
          return (
            <SwiperSlide key={index}>
              <div
                // initial={{ opacity: 0.6 }}
                // whileTap={{ scale: 0.9 }}
                // whileInView={{ opacity: 1 }}
                className="testimonial"
              >
                {/* <img src={client.img} alt="" /> */}
                <span>{client.cname}</span>
                <span>{client.dsg}</span>
                <span>{client.desc}</span>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </div>
  );
};

export default Testimonial;
