import React, { useEffect } from 'react';
import WOW from 'wowjs';
import $ from 'jquery';
// import { location } from 'window';


const Home = () => {

  useEffect(() => {
    const wow = new WOW.WOW({
      boxClass: 'wow', // default value
      animateClass: 'animated', // default value
      offset: 0, // default value
      mobile: true, // default value
      live: true // default value
    });
    wow.init();
  }, []);

  useEffect(()=>{
    (function ($) {
	
    
      // // Header Type = Fixed
      // $(window).scroll(function() {
      //   var scroll = $(window).scrollTop();
      //   var box = $('.header-text').height();
      //   var header = $('header').height();
    
      //   if (scroll >= box - header) {
      //     $("header").addClass("background-header");
      //   } else {
      //     $("header").removeClass("background-header");
      //   }
      // });
    
    
      // $('.owl-our-team').owlCarousel({
      //   items:3,
      //   loop:true,
      //   dots: true,
      //   nav: false,
      //   autoplay: true,
      //   margin:0,
      //     responsive:{
      //       0:{
      //         items:1
      //       },
      //       600:{
      //         items:2
      //       },
      //       1000:{
      //         items:3
      //       },
      //       1600:{
      //         items:3
      //       }
      //     }
      // })
      
    
      // Menu Dropdown Toggle
      if($('.menu-trigger').length){
        $(".menu-trigger").on('click', function() { 
          $(this).toggleClass('active');
          $('.header-area .nav').slideToggle(200);
        });
      }
    
    
     // Menu elevator animation
    $('a[href*=\\#]:not([href=\\#])').on('click', function() {
      if (window.location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && window.location.hostname == this.hostname) {
        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
        if (target.length) {
          var width = $(window).width();
          if(width < 991) {
            $('.menu-trigger').removeClass('active');
            $('.header-area .nav').slideUp(200);  
          }       
          $('html,body').animate({
            scrollTop: (target.offset().top) + 1
          }, 700);
          return false;
        }
      }
    });
    
      $(document).ready(function () {
          $(document).on("scroll", onScroll);
          
          //smoothscroll
          $('.scroll-to-section a[href^="#"]').on('click', function (e) {
              e.preventDefault();
              $(document).off("scroll");
              
              $('.scroll-to-section a').each(function () {
                  $(this).removeClass('active');
              })
              $(this).addClass('active');
            
              var target = this.hash,
              menu = target;
              var target = $(this.hash);
              $('html, body').stop().animate({
                  scrollTop: (target.offset().top) + 1
              }, 500, 'swing', function () {
                  window.location.hash = target;
                  $(document).on("scroll", onScroll);
              });
          });
      });
    
      function onScroll(event){
          var scrollPos = $(document).scrollTop();
          $('.nav a').each(function () {
              var currLink = $(this);
              var refElement = $(currLink.attr("href"));
              if (refElement.position().top <= scrollPos && refElement.position().top + refElement.height() > scrollPos) {
                  $('.nav ul li a').removeClass("active");
                  currLink.addClass("active");
              }
              else{
                  currLink.removeClass("active");
              }
          });
      }
    
    
    
      // Page loading animation
       $(window).on('load', function() {
    
            $('#js-preloader').addClass('loaded');
    
        });
    
      
    
      // Window Resize Mobile Menu Fix
      function mobileNav() {
        var width = $(window).width();
        $('.submenu').on('click', function() {
          if(width < 767) {
            $('.submenu ul').removeClass('active');
            $(this).find('ul').toggleClass('active');
          }
        });
      }
    
    
    
    
    })(window.jQuery);
  },[])

  return (
    <>
{/* //    <!-- ***** Preloader Start ***** --> */}
  {/* <div id="js-preloader" className="js-preloader">
  <div className="preloader-inner">
    <span className="dot"></span>
    <div className="dots">
      <span></span>
      <span></span>
      <span></span>
    </div>
  </div>
</div> */}
{/* <!-- ***** Preloader End ***** --> */}

{/* <!-- ***** Header Area Start ***** --> */}
<header className="header-area header-sticky wow slideInDown" data-wow-duration="0.75s" data-wow-delay="0s">
  <div className="container">
    <div className="row">
      <div className="col-12">
        <nav className="main-nav">
          <a href="index.html" className="logo">
            <h4>SC<span>Boost</span></h4>
          </a>
          <ul className="nav">
            <li className="scroll-to-section"><a href="#top" className="active">Home</a></li>
            <li className="scroll-to-section"><a href="#about">About Us</a></li>
            <li className="scroll-to-section"><a href="#services">Services</a></li>
            <li className="scroll-to-section"><a href="#portfolio">Portfolio</a></li>
            <li className="scroll-to-section"><a href="#contact">Message Us</a></li> 
            <li className="scroll-to-section"><div className="main-red-button"><a href="#contact">Contact Now</a></div></li> 
          </ul>        
          <span href="/" className='menu-trigger'>
              <span>Menu</span>
          </span>
        </nav>
      </div>
    </div>
  </div>
</header>
{/* <!-- ***** Header Area End ***** --> */}

<div className="main-banner wow fadeIn" id="top" data-wow-duration="1s" data-wow-delay="0.5s">
  <div className="container">
    <div className="row">
      <div className="col-lg-12">
        <div className="row">
          <div className="col-lg-6 align-self-center">
            <div className="left-content header-text wow fadeInLeft" data-wow-duration="1s" data-wow-delay="1s">
              <h6>Welcome to SC Boost</h6>
              <h2>We Make <em>Digital Ideas</em> &amp; <span>Scoial Media</span> Marketing</h2>
              <p>SC Boost is a professional looking HTML template using a Bootstrap 5 (beta 2). This CSS template is free for you provided by.</p>
            </div>
          </div>
          <div className="col-lg-6">
            <div className="right-image wow fadeInRight" data-wow-duration="1s" data-wow-delay="0.5s">
              <img src="assets/images/banner-right-image.png" alt="team meeting"/>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
</>
  )
}

export default Home
